mapper.select:
SELECT id, category_key, color_series_key, title, description, size, color, price, stock, weight, image_url, sort, status, create_time, update_time FROM diy_material WHERE 1=1
AND category_key IN
#{cat}
AND color_series_key IN
#{color}
ORDER BY sort ASC, create_time DESC

SELECT id, category_key, color_series_key, title, description, size, color, price, stock, weight, image_url, sort, status, create_time, update_time FROM diy_material WHERE 1=1
AND category_key IN
#{cat}
AND color_series_key IN
#{color}
ORDER BY title ASC, (size IS NULL) ASC, size ASC, sort ASC, create_time DESC

mapper.update:
UPDATE diy_material 
SET stock = stock - #{quantity}, 
    update_time = NOW() 
WHERE id = #{materialId} 
  AND stock >= #{quantity}
